import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaintBrush, faCode, faBrain } from '@fortawesome/free-solid-svg-icons';
import { faReact, faJs } from '@fortawesome/free-brands-svg-icons';

gsap.registerPlugin(ScrollTrigger);

const courses = [
  { id: 1, title: 'Introduction to React', image: 'data:image/jpeg;base64,', path: '/courses/react', icon: faReact },
  { id: 2, title: 'Advanced JavaScript', image: 'https://placekitten.com/301/200', path: '/courses/javascript', icon: faJs },
  { id: 3, title: 'UI/UX Design Principles', image: 'https://placekitten.com/302/200', path: '/courses/ui-ux', icon: faPaintBrush },
  { id: 4, title: 'Data Structures & Algorithms', image: 'data:image/jpeg;base64,', path: '/courses/dsa', icon: faCode },
  { id: 5, title: 'Data Structures & Algorithms', image: 'https://placekitten.com/303/200', path: '/courses/dsa', icon: faCode },
  { id: 6, title: 'Machine Learning Basics', image: 'data:image/jpeg;base64,', path: '/courses/ml', icon: faBrain },
];

const Course = () => {
  const sectionRef = useRef(null);
  const cardsContainerRef = useRef(null);

  useEffect(() => {
    const cardsContainer = cardsContainerRef.current;
    const cardWidth = cardsContainer.children[0].offsetWidth;
    const totalWidth = cardWidth * courses.length;

    // Clone cards for infinite effect
    const clonedCards = Array.from(cardsContainer.children).map(card => card.cloneNode(true));
    clonedCards.forEach(card => cardsContainer.appendChild(card));

    // Set up infinite scroll animation
    gsap.to(cardsContainer, {
      x: `-=${totalWidth}`,
      ease: 'none',
      duration: 30, // Adjust this value to control scroll speed
      repeat: -1,
      modifiers: {
        x: gsap.utils.unitize(x => parseFloat(x) % totalWidth)
      }
    });

    // Pause on hover
    cardsContainer.addEventListener('mouseenter', () => gsap.to(cardsContainer, { timeScale: 0, duration: 0.5 }));
    cardsContainer.addEventListener('mouseleave', () => gsap.to(cardsContainer, { timeScale: 1, duration: 0.5 }));

    return () => {
      gsap.killTweensOf(cardsContainer);
    };
  }, []);

  const handleCardClick = (path) => {
    console.log(`Navigating to: ${path}`);
  };

  return (
    <section ref={sectionRef} className="py-20 overflow-hidden bg-gradient-to-br from-blue-50 to-white">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-12 text-blue-900">Popular Courses</h2>
        <div className="relative overflow-hidden">
          <div ref={cardsContainerRef} className="flex space-x-8 py-8">
            {courses.map((course, index) => (
              <div
                key={`${course.id}-${index}`}
                className="flex-shrink-0 w-80 bg-white rounded-xl overflow-hidden shadow-lg transform transition-all duration-300 hover:shadow-xl cursor-pointer"
                onClick={() => handleCardClick(course.path)}
              >
                <div className="relative">
                  <img src={course.image} alt={course.title} className="w-full h-48 object-cover" />
                  <div className="tag absolute top-4 right-4 bg-blue-600 text-white text-xs px-2 py-1 rounded-full">
                    Popular
                  </div>
                </div>
                <div className="p-6">
                  <FontAwesomeIcon icon={course.icon} className="text-3xl text-blue-600 mb-4" />
                  <h3 className="text-xl font-semibold mb-2 text-blue-900">{course.title}</h3>
                  <p className="text-blue-700">Expand your knowledge and skills in this exciting course.</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Course;